import React, { useEffect, useState } from 'react'
import axios from 'axios'
import CigarCard from './CigarCard'


const AllCigars = (props) => {

    const { cigarList, setCigarList } = props
    const [ brand, setBrand ] = useState('')
    const [ name, setName ] = useState('')
    const [ description, setDescription ] = useState('')
    const [ image, setImage ] = useState('')
    const [ rating, setRating ] = useState('')
    const [ cigarId, setCigarId ] = useState('')

    useEffect(() => {
        const getCigars = async () => {
            try {
                const res = await axios.get(`http://localhost:8000/api/cigars`)
                console.log(res)
                console.log(res.data)
                setCigarList(res.data)
                setBrand(res.data.brand)
                setName(res.data.name)
                setDescription(res.data.description)
                setImage(res.data.img)
                setRating(res.data.rating)
            } catch (error) {
                console.log(error)
            }
        }
        getCigars()
    }, [])

    
    

    return (
        <div>
        

        { 
            cigarList.map((cigar, index) => (
                <CigarCard brand={brand} name={name} description={description} image={image} rating={rating} />
            ))
        }

        </div>
    )
}

export default AllCigars
