                    <div className='font-bold' style={{color:'#9aab89'}}>
                        <p>{cigar.brand}</p>
                        <p>{cigar.name}</p>
                    </div>
                    <img src={cigar.img} alt="cigar" className='w-full' />
                    <div className='text-left px-6 py-4'>
                        <p className='text-lg '>{cigar.description}</p>
                        <p className='font-bold'>Rating: {cigar.rating}/5</p>
                    </div>
                    
                </div>
        
    )
}

export default CigarCard
