import React, { useState } from 'react'
import styles from '../styles/CigarCard.module.css'
import { HiOutlineDotsVertical } from 'react-icons/hi'
import menuStyles from '../styles/MenuBtn.module.css'




const CigarCard = (props) => {

    const { brand, name, description, img, rating } = props

    
    

    return (
        
                <div className={styles.container}>
                    <div className={styles.cardHeader}>
                        <div className={styles.cardHeaderText}>
                            <p>{brand}</p>
                            <p>{name}</p>
                        </div>
                        <div className={styles.cardMenu}>
                            <MenuBtn icon={<HiOutlineDotsVertical style={{fontSize:'18px'}}/>}>
                                <p>Hello World!</p>
                            </MenuBtn>
                        </div>
                    </div>
                    
                    <img src={img} alt="cigar" className={styles.cardImg} />
                    <p className={styles.description}>{description}</p>
                    <p className={styles.rating}>Rating: {rating}/5</p>
                    
                </div>
        
    )
}

const MenuBtn = (props) => {

    const [open, setOpen] = useState(false)

    return (
        <div>
            <button className={menustyles.menuBtn} onClick = {() => setOpen(!open)}>{ props.icon }</button>
            { open && props.children }
        </div>
    )
}

const DropdownMenu = () => {


    return (
        <div className="dropdown">

        </div>
    )
}

export default CigarCard
