import React, { useEffect, useState } from 'react'
import axios from 'axios'
import CigarCard from './CigarCard'


const AllCigars = (props) => {

    const { cigarList, setCigarList } = props
    const [ brand, setBrand ] = useState('')
    const [ name, setName ] = useState('')
    const [ description, setDescription ] = useState('')
    const [ image, setImage ] = useState('')
    const [ rating, setRating ] = useState('')
    const [ cigarId, setCigarId ] = useState('')

    useEffect(() => {
        const getCigars = async () => {
            try {
                const res = await axios.get(`http://localhost:8000/api/cigars`)
                console.log(res)
                console.log(res.data)
                setCigarList(res.data)
                
            } catch (error) {
                console.log(error)
            }
        }
        getCigars()
    }, [])

    
    

    return (
        <div>
        

        { 
            cigarList.map(cigar => (
                <CigarCard 
                brand={cigar.brand} 
                name={cigar.name} 
                description={cigar.description} 
                img={cigar.img} 
                rating={cigar.rating} 
                key={cigar.id}
                />
            ))
        }

        </div>
    )
}

export default AllCigars
