import React, { useState } from 'react'
import { IconButton, ListItemIcon, Menu, MenuItem } from '@mui/material'
import styles from '../styles/CigarCard.module.css'
import MoreVertIcon from '@mui/icons-material/MoreVert'
import EditIcon from '@mui/icons-material/Edit'
import DeleteIcon from '@mui/icons-material/Delete'
import axios from 'axios'




const CigarCard = (props) => {

    const { cigarList, setCigarList, brand, name, description, img, rating, id } = props

    const [anchorEl, setAnchorEl] = React.useState(null);
    const open = Boolean(anchorEl);
    const handleClick = (event) => {
        setAnchorEl(event.currentTarget);
    };
    const handleClose = () => {
        setAnchorEl(null);
    };
    
    
const deleteFilter = (idFromBelow) => {
    axios.delete(`http://localhost:8000/api/cigars/${id}`)
        .then((res) => {
            console.log(res.data)
            setCigarList(cigarList.filter((cigar, index) => cigar.id !== idFromBelow))
            this.forceUpdate()
        })
        .catch((err) => {
            console.log(err)
        })
}

    return (
        
                <div className={styles.container}>
                    <div className={styles.cardHeader}>
                        <div className={styles.cardHeaderText}>
                            <p>{brand}</p>
                            <p>{name}</p>
                        </div>
                        <div className={styles.cardMenu}>
                            <IconButton
                            aria-label='Options'
                            id='long-button'
                            aria-controls={open ? 'long-menu' : undefined}
                            aria-expanded={open ? 'true' : undefined}
                            aria-haspopup='true'
                            onClick={handleClick}
                            >
                            <MoreVertIcon/>
                            </IconButton>
                            <Menu
                            id='long-menu'
                            MenuListProps={{
                                'aria-labelledby': 'long-button',
                            }}
                            anchorEl={anchorEl}
                            open={open}
                            onClose={handleClose}
                            >
                            <MenuItem>
                                <ListItemIcon>
                                    <EditIcon fontSize='small'/>
                                </ListItemIcon>
                                Edit Cigar
                            </MenuItem>
                            <MenuItem onClick={()=>deleteFilter(id)}>
                                <ListItemIcon >
                                    <DeleteIcon fontSize='small'/>
                                </ListItemIcon>
                                Delete Cigar
                            </MenuItem>

                            </Menu>
                        </div>
                    </div>
                    
                    <img src={img} alt="cigar" className={styles.cardImg} />
                    <p className={styles.description}>{description}</p>
                    <p className={styles.rating}>Rating: {rating}/5</p>
                    
                </div>
        
    )
}



export default CigarCard
