import React, { useEffect } from 'react'
import axios from 'axios'


const AllCigars = (props) => {

    const { cigarList, setCigarList } = props

    

    return (
        <div className='w-3/4 m-auto flex flex-wrap-reverse '>
        

        { 
            cigarList.map((cigar, index) => (
                <div key={cigar._id} className='w-1/4 p-10 m-auto  shadow border-solid border border-slate-300' >
                    <p className='text-lg p-5 w-1/2 m-auto'>{cigar.brand}</p>
                    <p className='text-lg p-5 w-1/2 m-auto'>{cigar.name}</p>
                    <p className='text-lg p-5 w-1/2 m-auto'>{cigar.description}</p>
                    <img src={cigar.img} alt="cigar" className='m-auto w-1/2' />
                    <p className='text-lg p-5 w-1/2 m-auto'>{cigar.rating}/5</p>
                </div>
            ))
        }

        </div>
    )
}

export default AllCigars
