                        setDescription('')
                        ref.current.value = null
                        
            } catch (error) {
                console.log(error)
            }
        
    }

    return (
        
        <div >
            <NavBar setSearchTerm={setSearchTerm}/>
            <div style={{width:'75%', margin:'0 auto'}}>
                <CigarForm 
                cigarList={cigarList} 
                setCigarList={setCigarList} 
                initialName=''
