                initialDescription=''
                initialImage=''
                initialRating=''
                onSubmitProp={createCigar}
                />

                <hr style={{margin:'0'}}/>

                <AllCigars 
                cigarList={cigarList} 
                setCigarList={setCigarList}
                searchTerm={searchTerm}
                />
            </div>
            

        </div>
    )
}

export default Main
