import React, { useEffect } from 'react'
import axios from 'axios'


const AllCigars = (props) => {

    const { cigarList, setCigarList } = props

    useEffect(() => {
        const getCigars = async () => {
            try {
                const res = await axios.get(`http://localhost:8000/api/cigars`)
                console.log(res)
                console.log(res.data)
                setCigarList(res.data)
            } catch (error) {
                console.log(error)
            }
        }
        getCigars()
    }, [])

    return (
        <div className='w-3/4 m-auto flex flex-wrap '>
        

        { 
            cigarList.map((cigar, index) => (
                <div key={cigar._id} className='w-1/4 min-h-full p-10 mx-5 my-10 shadow-lg border border-slate-300 rounded-lg' >
                    <divtext-left>
                        <p className='text-lg w-1/2'>{cigar.brand}</p>
                        <p className='text-lg w-1/2'>{cigar.name}</p>
                    </divtext-left>
                    <img src={cigar.img} alt="cigar" className='m-auto w-1/2' />
                    <div className='text-left'>
                        <p className='text-lg '>{cigar.description}</p>
                        <p className='text-lg '>{cigar.rating}/5</p>
                    </div>
                    
                </div>
            ))
        }

        </div>
    )
}

export default AllCigars
