import React, { useEffect } from 'react'
import axios from 'axios'


const AllCigars = (props) => {

    const { cigarList, setCigarList } = props

    useEffect(() => {
        
        axios.get(`http://localhost:8000/api/cigars`)
            .then((res) => {
                console.log(res)
                console.log(res.data)
                setCigarList(res.data)
            })
            .catch((err)=>{
                console.log(err)
            })
    }, [])

    return (
        <div>
        { 
            cigarList.map((cigar, index) => (
                <div key={cigar._id}>
                    <p>{cigar.brand}</p>
                    <p>{cigar.name}</p>
                    <p>{cigar.description}</p>
                    <img src={cigar.img} alt="cigar" />
                    <p>{cigar.rating}/5</p>
                </div>
            ))
        }

        </div>
    )
}

export default AllCigars
