import axios from 'axios'
import React, { useState, useEffect } from 'react'
import AllCigars from '../components/AllCigars'
import CigarForm from '../components/CigarForm'
import NavBar from '../components/NavBar'


const Main = () => {

    const [cigarList, setCigarList] = useState([])
    const [searchTerm, setSearchTerm] = useState('')


    useEffect(() => {
        const getCigars = async () => {
            try {
                const res = await axios.get(`http://localhost:8000/api/cigars`)
                console.log(res)
                console.log(res.data)
                setCigarList(res.data)
                
            } catch (error) {
                console.log(error)
            }
        }
        getCigars()
    }, [])

    const deleteFilter = (idFromBelow) => {
        axios.delete(`http://localhost:8000/api/cigars/${idFromBelow}`)
            .then((res) => {
                console.log(res.data)
                setCigarList(cigarList.filter(cigar => cigar._id !== idFromBelow))
                console.log(idFromBelow)
            })
            .catch((err) => {
                console.log(err)
            })
    }
    

    const createCigar = async (cigarParam) => {
        e.preventDefault()
            try {
                const data = new FormData()
                data.append('file', image)
                data.append('upload_preset', 'cigar_app')
                const res = await axios.post('https://api.cloudinary.com/v1_1/dmsqthdn3/image/upload', data)
                console.log(res.data)
                setImage(res.data.url)

                const res2 = await axios.post(`http://localhost:8000/api/cigars`,{
                            productParam,
                            img: res.data.url,
                            
                        })
                        console.log(res2.data)
                        setCigarList([ res2.data, ...cigarList ])
                        setBrand('')
                        setName('')
                        setDescription('')
                        setImage('')
                        setRating('')
            } catch (error) {
                console.log(error)
            }
        
    }


    return (
        
        <div >
            <NavBar setSearchTerm={setSearchTerm}/>
            <div style={{width:'75%', margin:'0 auto'}}>
                <CigarForm 
                onSubmitProp={createCigar}
                deleteFilter={deleteFilter}
                initialName=""
                initialBrand=""
                intialDescription=""
                initalRating=""
                />

                <hr style={{margin:'0'}}/>

                <AllCigars 
                cigarList={cigarList} 
                setCigarList={setCigarList}
                searchTerm={searchTerm}
                />
            </div>
            

        </div>
    )
}

export default Main
